import ReactDOM from 'react-dom'
import React, { Component } from 'react'

import { w3cwebsocket as W3CWebSocket } from "websocket"

import Identicon from 'react-identicons'

import { Card, Avatar, Input, Typography } from 'antd'
import 'antd/dist/antd.css'
import "./index.css"
// import Search from 'antd/lib/input/Search'

const { Search } = Input
const { Text } = Typography
const { Meta } = Card



const client = new W3CWebSocket('ws://127.0.0.1:8000')

export default class App extends Component {


    state = {
        userName:'',
        isLoggedIn:false,
        messages: [],
        messageVal:'',
        identicon:null,

    }

    onLogIn = (value) => {
        const UTCtime = Date.now()
        const identicon = <Identicon string={this.userName+UTCtime}/>
        this.setState({userName:value, isLoggedIn:true, identicon:identicon})
    }

    onButtonClick = (msg) => {
        client.send(JSON.stringify({
            type:'message',
            msg:msg,
            user: this.state.userName
        }))
        this.setState({messageVal:''})
    }

    componentDidMount() {

        client.onopen = () => {
            console.log('WebSocket Client Connected')
        }

        client.onmessage = (message) => {

            const dataFromServer = JSON.parse(message.data)
            
            console.log('Received response from server: ', dataFromServer)
            if (dataFromServer.type === "message") {
                    this.setState((state) => 
                    ({
                        messages: [...state.messages,
                        {
                            msg: dataFromServer.msg,
                            user: dataFromServer.user
                        }]
                    })
                )
            }
        }
 
    }


    render() {
        return (
            <div className="main">

                {this.state.isLoggedIn ?
                <div>
                    {/* <button onClick={() => this.onButtonClick("Heelo")}>SEND DAT MSG </button>
                    
                    {this.state.messages.map(msg => <div><p>User {msg.user} says:</p><p>{msg.msg}</p></div>)} */}

                    <div className="title">
                        <Text type="secondary" style={{fontSize: '36px'}}>Websocket Chat App</Text>
                    </div>
                    <div className="message-block">
                        {this.state.messages.map(message => {


                            const cardUserClass = message.user === this.state.userName ? "own-message" : "other-message"
                            const avatarIcon = this.state.identicon ? this.state.identicon : message.user[0].toUpperCase()+message.user[1].toLowerCase()  
                            return (  
                                <Card key={message.msg} className={'message-card ' + cardUserClass}>
                                    <Meta
                                    
                                        avatar={ <Avatar className="avatar">{avatarIcon}</Avatar> }
                                        title={message.user}
                                        description={message.msg}

                                    />
                                </Card>
                            )}
                            )}
                    </div>
                    <div className="bottom">
                        <Search
                            placeholder="Enter message"
                            enterButton="Send"
                            value={this.state.messageVal}
                            size="large"
                            onChange={(e)=> this.setState({messageVal: e.target.value})}
                            onSearch={value=> this.onButtonClick(value)}
                        />
                    </div>

                </div> 
                :
                <div style={{padding:'200px 40px'}}>
                    <Search
                        placeholder="Enter A Username"
                        enterButton="Sign in"
                        size="large"
                        onSearch={value => this.onLogIn(value)}
                    />
                </div>
                }
                
            </div>
        )
    }
}

ReactDOM.render(<App />, document.getElementById('root'))
